    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;
    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;
    --primary: 217.2 91.2% 59.8%;
    --primary-foreground: 222.2 84% 4.9%;
    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;
    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;
    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;
    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 224.3 76.3% 94.1%;
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

@layer components {
  .adorable-gradient {
    @apply bg-gradient-to-r from-adorable-pink via-adorable-purple to-adorable-blue;
  }

  .adorable-card {
    @apply bg-white/80 backdrop-blur-sm border border-white/20 shadow-adorable rounded-2xl;
  }

  .adorable-button {
    @apply bg-gradient-to-r from-adorable-pink to-adorable-purple hover:from-adorable-purple hover:to-adorable-blue transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-adorable-lg;
  }

  .adorable-input {
    @apply border-2 border-gray-200 focus:border-adorable-blue focus:ring-2 focus:ring-adorable-blue/20 rounded-xl transition-all duration-200;
  }

  .workflow-step {
    @apply relative flex items-center justify-center w-12 h-12 rounded-full border-2 transition-all duration-300;
  }

  .workflow-step.active {
    @apply bg-adorable-blue border-adorable-blue text-white shadow-lg scale-110;
  }

  .workflow-step.completed {
    @apply bg-green-500 border-green-500 text-white;
  }

  .workflow-step.pending {
    @apply bg-gray-100 border-gray-300 text-gray-500;
  }

  .file-upload-zone {
    @apply border-2 border-dashed border-gray-300 rounded-2xl p-8 text-center transition-all duration-300 hover:border-adorable-blue hover:bg-adorable-blue/5;
  }

  .file-upload-zone.dragover {
    @apply border-adorable-blue bg-adorable-blue/10 scale-105;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  @keyframes glow {
    from { box-shadow: 0 0 20px rgba(255, 107, 157, 0.3); }
    to { box-shadow: 0 0 30px rgba(255, 107, 157, 0.6); }
  }
}

