    showUserModal(user) {
        // This would show a modal with user details
        alert(`D√©tails utilisateur:\nNom: ${user.firstName} ${user.lastName}\nEmail: ${user.email}\nR√¥le: ${user.role}`);
    }

    editUser(userId) {
        // This would show an edit modal
        alert(`√âdition utilisateur ID: ${userId}`);
    }
}

