import com.example.unifiedapi.dto.TtnSaveEfactRequest;
import com.example.unifiedapi.dto.TtnConsultEfactRequest;
import com.example.unifiedapi.dto.TtnConsultCriteriaRequest;
import com.example.unifiedapi.service.UserCredentialsService;
import com.example.unifiedapi.dto.TtnSaveEfactResponse;
import com.example.unifiedapi.dto.TtnConsultEfactResponse;
import com.example.unifiedapi.service.TtnOperationsService;
import jakarta.validation.Valid;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;
import org.springframework.web.multipart.MultipartFile;

import java.util.Arrays;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

@RestController
@RequestMapping("/ttn")
public class TtnOperationsController {

    private static final Logger logger = LoggerFactory.getLogger(TtnOperationsController.class);

    private final TtnOperationsService ttnOperationsService;
    private final UserCredentialsService userCredentialsService;

    @Autowired
    public TtnOperationsController(TtnOperationsService ttnOperationsService, UserCredentialsService userCredentialsService) {
        this.ttnOperationsService = ttnOperationsService;
        this.userCredentialsService = userCredentialsService;
    }

    @PostMapping(value = "/save-efact", consumes = MediaType.MULTIPART_FORM_DATA_VALUE)
    public ResponseEntity<?> saveEfact(
            @RequestParam("username") String username,
            @RequestParam("password") String password,
            @RequestParam("matriculeFiscal") String matriculeFiscal,
            @RequestParam("files") MultipartFile[] files) {

        logger.info("Received saveEfact request for user: {}, matricule: {}, files: {}", 
                   username, matriculeFiscal, files.length);

        try {
            // Validate input
            if (username == null || username.trim().isEmpty() ||
                password == null || password.trim().isEmpty() ||
                matriculeFiscal == null || matriculeFiscal.trim().isEmpty()) {

                Map<String, String> error = new HashMap<>();
                error.put("error", "Missing required fields: username, password, matriculeFiscal");
                return ResponseEntity.badRequest().body(error);
            }

            if (files == null || files.length == 0) {
                Map<String, String> error = new HashMap<>();
                error.put("error", "No XML files provided");
                return ResponseEntity.badRequest().body(error);
            }

            // Validate file types
            for (MultipartFile file : files) {
                if (file.isEmpty() || !file.getOriginalFilename().toLowerCase().endsWith(".xml")) {
                    Map<String, String> error = new HashMap<>();
                    error.put("error", "Only XML files are allowed");
                    return ResponseEntity.badRequest().body(error);
                }
            }

            // Create request object
            TtnSaveEfactRequest request = new TtnSaveEfactRequest(username, password, matriculeFiscal);

            // Process the request
            TtnSaveEfactResponse response = ttnOperationsService.saveEfact(request, Arrays.asList(files));

            return ResponseEntity.ok(response);

        } catch (Exception e) {
            logger.error("Error in saveEfact: {}", e.getMessage(), e);
            Map<String, String> error = new HashMap<>();
            error.put("error", "Failed to save e-invoices");
            error.put("details", e.getMessage());
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(error);
        }
    }

    /**
     * Consult e-invoices using stored user credentials
     */
    @PostMapping("/consult")
    public ResponseEntity<?> consultWithStoredCredentials(@RequestBody TtnConsultCriteriaRequest request) {
        logger.info("Received consult request using stored credentials");

        try {
            // Get credentials from authenticated user
            String username = userCredentialsService.getTtnUsername();
            String password = userCredentialsService.getTtnPassword();
            String matriculeFiscal = userCredentialsService.getTtnMatriculeFiscal();

            // Create full request with stored credentials
            TtnConsultEfactRequest fullRequest = new TtnConsultEfactRequest(username, password, matriculeFiscal, request.getCriteria());

            TtnConsultEfactResponse response = ttnOperationsService.consultEfact(fullRequest);

            if (response.isSuccess()) {
                return ResponseEntity.ok(response);
            } else {
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(response);
            }

        } catch (Exception e) {
            logger.error("Error in consult with stored credentials: {}", e.getMessage(), e);
            Map<String, String> error = new HashMap<>();
            error.put("error", "Failed to consult e-invoices");
            error.put("details", e.getMessage());
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(error);
        }
    }

    @PostMapping("/consult-efact")
    public ResponseEntity<?> consultEfact(@Valid @RequestBody TtnConsultEfactRequest request) {
        logger.info("Received consultEfact request for user: {}, matricule: {}", 
                   request.getUsername(), request.getMatriculeFiscal());

        try {
            TtnConsultEfactResponse response = ttnOperationsService.consultEfact(request);

            if (response.isSuccess()) {
                return ResponseEntity.ok(response);
            } else {
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(response);
            }

        } catch (Exception e) {
            logger.error("Error in consultEfact: {}", e.getMessage(), e);
            Map<String, String> error = new HashMap<>();
            error.put("error", "Failed to consult e-invoices");
            error.put("details", e.getMessage());
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(error);
        }
    }

    @GetMapping("/efact/{id}")
    public ResponseEntity<?> getEfactById(
            @PathVariable String id,
            @RequestParam("username") String username,
            @RequestParam("password") String password,
            @RequestParam("matriculeFiscal") String matriculeFiscal) {

        logger.info("Received getEfactById request for ID: {}, user: {}, matricule: {}", 
                   id, username, matriculeFiscal);

        try {
            // Validate input
            if (username == null || username.trim().isEmpty() ||
                password == null || password.trim().isEmpty() ||
                matriculeFiscal == null || matriculeFiscal.trim().isEmpty()) {

                Map<String, String> error = new HashMap<>();
                error.put("error", "Missing required query parameters: username, password, matriculeFiscal");
                return ResponseEntity.badRequest().body(error);
            }

            // Create criteria for specific ID lookup
            Map<String, Object> criteria = new HashMap<>();
            criteria.put("generatedRef", id);

            TtnConsultEfactRequest request = new TtnConsultEfactRequest(username, password, matriculeFiscal, criteria);
            TtnConsultEfactResponse response = ttnOperationsService.consultEfact(request);

            if (response.isSuccess()) {
                if (response.getInvoices() == null || response.getInvoices().isEmpty()) {
                    Map<String, String> error = new HashMap<>();
                    error.put("error", "E-invoice not found");
                    return ResponseEntity.status(HttpStatus.NOT_FOUND).body(error);
                }

                Map<String, Object> result = new HashMap<>();
                result.put("success", true);
                result.put("invoice", response.getInvoices().get(0));
                return ResponseEntity.ok(result);
            } else {
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(response);
            }

        } catch (Exception e) {
            logger.error("Error in getEfactById: {}", e.getMessage(), e);
            Map<String, String> error = new HashMap<>();
            error.put("error", "Failed to get e-invoice details");
            error.put("details", e.getMessage());
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(error);
        }
    }

    @GetMapping("/health")
    public ResponseEntity<Map<String, Object>> healthCheck() {
        logger.debug("Health check requested");

        Map<String, Object> health = new HashMap<>();
        health.put("status", "OK");
        health.put("service", "TTN Operations API");
        health.put("timestamp", java.time.Instant.now().toString());

        return ResponseEntity.ok(health);
    }
}

package com.example.unifiedapi.controller;

import com.example.unifiedapi.dto.TtnConsultEfactRequest;
import com.example.unifiedapi.dto.TtnConsultEfactResponse;
import com.example.unifiedapi.service.TtnOperationsService;
import com.example.unifiedapi.service.TtnTransformationService;
import com.example.unifiedapi.service.UserCredentialsService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.util.HashMap;
import java.util.Map;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
@RestController
@RequestMapping("/transform-ttn-invoice")
@CrossOrigin(origins = "*")
public class TtnTransformController {
    private static final Logger logger = LoggerFactory.getLogger(TtnTransformController.class);

    private final TtnOperationsService ttnOperationsService;
    private final TtnTransformationService ttnTransformationService;
    private final UserCredentialsService userCredentialsService;

    @Autowired
    public TtnTransformController(TtnOperationsService ttnOperationsService, TtnTransformationService ttnTransformationService, UserCredentialsService userCredentialsService) {
        this.ttnOperationsService = ttnOperationsService;
        this.ttnTransformationService = ttnTransformationService;
        this.userCredentialsService = userCredentialsService;
    }

    @GetMapping
    public ResponseEntity<?> transformTtnInvoice(
        @RequestParam String ttnInvoiceId,
        @RequestParam(required = false) String filename
    ) {
    logger.info("[TTN TRANSFORM] Received request for ttnInvoiceId={} filename={}", ttnInvoiceId, filename);
    Map<String, Object> result = new HashMap<>();
        try {
            // 1. Get TTN credentials for the current user
            String ttnUsername = userCredentialsService.getTtnUsername();
            String ttnPassword = userCredentialsService.getTtnPassword();
            String ttnMatriculeFiscal = userCredentialsService.getTtnMatriculeFiscal();

            // 2. Consult TTN for the invoice
            TtnConsultEfactRequest consultRequest = new TtnConsultEfactRequest();
            consultRequest.setUsername(ttnUsername);
            consultRequest.setPassword(ttnPassword);
            consultRequest.setMatriculeFiscal(ttnMatriculeFiscal);
            consultRequest.setCriteria(ttnInvoiceId);

            TtnConsultEfactResponse consultResponse = ttnOperationsService.consultEfact(consultRequest);
            String rawResponse = consultResponse.getRawResponse();

            // 3. Extract <xmlContent> from the SOAP response
            String xmlContent = com.example.unifiedapi.util.XmlUtils.extractXmlContent(rawResponse);
            logger.info("xml----------------------------------------",xmlContent);
            if (xmlContent == null || xmlContent.isEmpty()) {
                result.put("success", false);
                result.put("message", "xmlContent not available yet");
                return ResponseEntity.ok(result);
            }

            // 4. Transform to HTML
            String html = ttnTransformationService.transformXmlToHtml(
                    xmlContent, ttnUsername, ttnPassword, ttnMatriculeFiscal, filename != null ? filename : ttnInvoiceId
            );
            // 5. Return a link or the HTML content (for demo, return HTML directly)
            result.put("success", true);
            result.put("html", html);
            // Optionally, you could save the HTML and return a URL
            return ResponseEntity.ok(result);
        } catch (Exception e) {
                        logger.error("[TTN TRANSFORM] Error processing ttnInvoiceId={}", ttnInvoiceId, e);

            result.put("success", false);
            result.put("message", e.getMessage());
            return ResponseEntity.status(500).body(result);
        }
    }
}

package com.example.unifiedapi.controller;

import com.example.unifiedapi.service.OperationLogService;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.time.LocalDateTime;
import java.util.HashMap;
import java.util.Map;

@RestController
@RequestMapping("/")
public class UnifiedApiController {

    private static final Logger logger = LoggerFactory.getLogger(UnifiedApiController.class);

    private final OperationLogService operationLogService;

    @Autowired
    public UnifiedApiController(OperationLogService operationLogService) {
        this.operationLogService = operationLogService;
    }

    @GetMapping("/health")
    public ResponseEntity<Map<String, Object>> globalHealthCheck() {
        logger.debug("Global health check requested");

        Map<String, Object> health = new HashMap<>();
        health.put("status", "OK");
        health.put("service", "Unified Operations API");
        health.put("version", "1.0.0");
        health.put("timestamp", java.time.Instant.now().toString());
        health.put("description", "Unified API for TTN E-Facturation and ANCE SEAL XML Signature Operations");

        // Add service endpoints
        Map<String, Object> endpoints = new HashMap<>();
        endpoints.put("ttn", Map.of(
            "saveEfact", "POST /api/v1/ttn/save-efact",
            "consultEfact", "POST /api/v1/ttn/consult-efact",
            "getEfact", "GET /api/v1/ttn/efact/{id}",
            "health", "GET /api/v1/ttn/health"
        ));
        endpoints.put("anceSeal", Map.of(
            "sign", "POST /api/v1/ance-seal/sign",
            "validate", "POST /api/v1/ance-seal/validate",
            "batchSign", "POST /api/v1/ance-seal/batch/sign",
            "health", "GET /api/v1/ance-seal/health"
        ));
        health.put("availableEndpoints", endpoints);

        return ResponseEntity.ok(health);
    }

    @GetMapping("/stats")
    public ResponseEntity<Map<String, Object>> getOperationStats() {
        logger.debug("Operation statistics requested");

        Map<String, Object> stats = new HashMap<>();

        // TTN Operations Stats
        long ttnSaveCount = operationLogService.countByOperationType("TTN_SAVE");
        long ttnConsultCount = operationLogService.countByOperationType("TTN_CONSULT");

        // ANCE SEAL Operations Stats
        long anceSignCount = operationLogService.countByOperationType("ANCE_SIGN");
        long anceValidateCount = operationLogService.countByOperationType("ANCE_VALIDATE");
        long anceBatchSignCount = operationLogService.countByOperationType("ANCE_BATCH_SIGN");

        // Overall Stats
        long totalOperations = operationLogService.countAll();
        long successfulOperations = operationLogService.countByStatus("SUCCESS");
        long failedOperations = operationLogService.countByStatus("FAILURE");
        double successRate = totalOperations > 0 ? (100.0 * successfulOperations / totalOperations) : 0.0;

        Map<String, Object> ttnStats = new HashMap<>();
        ttnStats.put("saveEfact", ttnSaveCount);
        ttnStats.put("consultEfact", ttnConsultCount);
        ttnStats.put("total", ttnSaveCount + ttnConsultCount);

        Map<String, Object> anceSealStats = new HashMap<>();
        anceSealStats.put("sign", anceSignCount);
        anceSealStats.put("validate", anceValidateCount);
        anceSealStats.put("batchSign", anceBatchSignCount);
        anceSealStats.put("total", anceSignCount + anceValidateCount + anceBatchSignCount);

        Map<String, Object> overallStats = new HashMap<>();
        overallStats.put("totalOperations", totalOperations);
        overallStats.put("successfulOperations", successfulOperations);
        overallStats.put("failedOperations", failedOperations);
        overallStats.put("successRate", String.format("%.1f%%", successRate));

        stats.put("ttn", ttnStats);
        stats.put("anceSeal", anceSealStats);
        stats.put("overall", overallStats);
        stats.put("timestamp", java.time.Instant.now().toString());

        return ResponseEntity.ok(stats);
    }

    @GetMapping("/recent-operations")
    public ResponseEntity<Map<String, Object>> getRecentOperations(
            @RequestParam(defaultValue = "10") int limit) {
        logger.debug("Recent operations requested with limit: {}", limit);

        Map<String, Object> response = new HashMap<>();
        response.put("operations", operationLogService.getRecentOperations(limit));
        response.put("timestamp", java.time.Instant.now().toString());

        return ResponseEntity.ok(response);
    }

    @GetMapping("/operations/by-type/{operationType}")
    public ResponseEntity<Map<String, Object>> getOperationsByType(
            @PathVariable String operationType) {
        logger.debug("Operations by type requested: {}", operationType);

        Map<String, Object> response = new HashMap<>();
        response.put("operationType", operationType);
        response.put("operations", operationLogService.getOperationsByType(operationType));
        response.put("count", operationLogService.countByOperationType(operationType));
        response.put("timestamp", java.time.Instant.now().toString());

        return ResponseEntity.ok(response);
    }

    @GetMapping("/operations/by-user/{username}")
    public ResponseEntity<Map<String, Object>> getOperationsByUser(
            @PathVariable String username) {
        logger.debug("Operations by user requested: {}", username);

        Map<String, Object> response = new HashMap<>();
        response.put("username", username);
        // Note: This endpoint would need User lookup to work with new entity structure
        // For now, return empty list to avoid compilation error
        response.put("operations", java.util.Collections.emptyList());
        response.put("timestamp", java.time.Instant.now().toString());
        response.put("message", "User operations endpoint needs User entity lookup");

        return ResponseEntity.ok(response);
    }

    @GetMapping("/operations/since/{hours}")
    public ResponseEntity<Map<String, Object>> getOperationsSince(
            @PathVariable int hours) {
        logger.debug("Operations since {} hours requested", hours);

        LocalDateTime since = LocalDateTime.now().minusHours(hours);

        Map<String, Object> response = new HashMap<>();
        response.put("since", since.toString());
        response.put("operations", operationLogService.getRecentOperations(since));
        response.put("timestamp", java.time.Instant.now().toString());

        return ResponseEntity.ok(response);
    }

    @ExceptionHandler(Exception.class)
    public ResponseEntity<Map<String, Object>> handleGlobalException(Exception e) {
        logger.error("Unhandled exception in unified API: {}", e.getMessage(), e);

        Map<String, Object> error = new HashMap<>();
        error.put("error", "Internal server error");
        error.put("message", e.getMessage());
        error.put("timestamp", java.time.Instant.now().toString());

        return ResponseEntity.internalServerError().body(error);
    }
}

