package com.example.unifiedapi.security;

import com.example.unifiedapi.service.CustomUserDetailsService;
import com.example.unifiedapi.util.JwtUtil;
import jakarta.servlet.FilterChain;
import jakarta.servlet.ServletException;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;
import org.springframework.security.core.context.SecurityContextHolder;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.web.authentication.WebAuthenticationDetailsSource;
import org.springframework.stereotype.Component;
import org.springframework.util.StringUtils;
import org.springframework.web.filter.OncePerRequestFilter;

import java.io.IOException;

@Component
public class JwtAuthenticationFilter extends OncePerRequestFilter {

    private static final Logger logger = LoggerFactory.getLogger(JwtAuthenticationFilter.class);

    @Autowired
    private JwtUtil jwtUtil;

    @Autowired
    private CustomUserDetailsService customUserDetailsService;

    @Override
    protected void doFilterInternal(HttpServletRequest request, HttpServletResponse response,
                                  FilterChain filterChain) throws ServletException, IOException {

        // Skip JWT processing for public auth endpoints only (signin, signup, etc.)
        String path = request.getRequestURI();
        if (path.contains("/auth/signin") || path.contains("/auth/signup") ||
            path.contains("/auth/fix-admin-password") || path.contains("/auth/debug-user") ||
            path.contains("/auth/check-user")) {
            logger.debug("Skipping JWT filter for public auth endpoint: {}", path);
            filterChain.doFilter(request, response);
            return;
        }

        try {
            String jwt = getJwtFromRequest(request);

            if (StringUtils.hasText(jwt) && jwtUtil.validateToken(jwt)) {
                String username = jwtUtil.extractUsername(jwt);

                UserDetails userDetails = customUserDetailsService.loadUserByUsername(username);

                if (jwtUtil.validateToken(jwt, userDetails)) {
                    UsernamePasswordAuthenticationToken authentication =
                        new UsernamePasswordAuthenticationToken(userDetails, null, userDetails.getAuthorities());
                    authentication.setDetails(new WebAuthenticationDetailsSource().buildDetails(request));

                    SecurityContextHolder.getContext().setAuthentication(authentication);
                    logger.debug("Set authentication for user: {}", username);
                }
            }
        } catch (Exception ex) {
            logger.error("Could not set user authentication in security context", ex);
        }

        filterChain.doFilter(request, response);
    }

    private String getJwtFromRequest(HttpServletRequest request) {
        String bearerToken = request.getHeader("Authorization");
        if (StringUtils.hasText(bearerToken) && bearerToken.startsWith("Bearer ")) {
            return bearerToken.substring(7);
        }
        return null;
    }
}

package com.example.unifiedapi.service;
import com.example.unifiedapi.entity.User;
import java.io.FileWriter;
import java.io.IOException;
import java.nio.charset.StandardCharsets;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.Base64;
import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.CompletableFuture;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.http.HttpEntity;
import org.springframework.http.HttpHeaders;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.scheduling.annotation.Async;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;

import com.fasterxml.jackson.databind.ObjectMapper;

@Service
public class AnceSealClient {

    @Value("${ance.seal.signUrl}")
    private String signUrlTemplate; // e.g. https://193.95.63.230/tunsign-proxy-webapp/services/rest/tunsign-proxy/signHash/{alias}/SHA256

    @Value("${ance.seal.pin}")
    private String pin;

    @Value("${ance.seal.validationUrl}")
    private String validationUrl;

    @Value("${ance.seal.reportsDir:reports}")
    private String reportsDirectory;

    private final RestTemplate restTemplate;
    private final ObjectMapper objectMapper;
    private final UserCredentialsService userCredentialsService;

    @Autowired
    public AnceSealClient(RestTemplate restTemplate, ObjectMapper objectMapper, UserCredentialsService userCredentialsService) {
        this.restTemplate = restTemplate;
        this.objectMapper = objectMapper;
        this.userCredentialsService = userCredentialsService;
    }

    /**
     * Calls the ANCE SEAL API to validate the XML signature and saves the validation details as a JSON file.
     *
     * @param xmlContent The XML content to validate.
     * @return The path to the saved JSON validation report file.
     * @throws Exception if validation or saving fails.
     */
    public String validateSignatureWithResponse(String xmlContent) throws Exception {
        return validateSignatureWithResponse(xmlContent, null);
    }

    public String validateSignatureWithResponse(String xmlContent, String ttnInvoiceId) throws Exception {
        // Encode XML content in Base64
        String base64Xml = Base64.getEncoder().encodeToString(xmlContent.getBytes(StandardCharsets.UTF_8));

        Map<String, Object> mimeTypeMap = new HashMap<>();
        mimeTypeMap.put("mimeTypeString", "text/xml");

        Map<String, Object> signedDocument = new HashMap<>();
        signedDocument.put("bytes", base64Xml);
        signedDocument.put("name", "signed.xml");
        signedDocument.put("mimeType", mimeTypeMap);

        Map<String, Object> payload = new HashMap<>();
        payload.put("signedDocument", signedDocument);

        HttpHeaders headers = new HttpHeaders();
        headers.setContentType(MediaType.APPLICATION_JSON);
        HttpEntity<Map<String, Object>> request = new HttpEntity<>(payload, headers);

        // Call the ANCE validation API
        ResponseEntity<String> response = restTemplate.postForEntity(validationUrl, request, String.class);
        String rawBody = response.getBody();

        if (!response.getStatusCode().is2xxSuccessful() || rawBody == null) {
            throw new RuntimeException("ANCE SEAL validation failed: " + response.getStatusCode());
        }

        // Parse the raw ANCE response to add TTN invoice ID if needed
        String finalResponse;
        if (ttnInvoiceId != null && !ttnInvoiceId.isEmpty()) {
            // Parse the ANCE JSON response and add TTN information
            @SuppressWarnings("unchecked")
            Map<String, Object> anceResponse = objectMapper.readValue(rawBody, Map.class);
            anceResponse.put("ttnInvoiceId", ttnInvoiceId);
            anceResponse.put("ttnStatus", "Successfully saved to TTN with ID: " + ttnInvoiceId);
            finalResponse = objectMapper.writerWithDefaultPrettyPrinter().writeValueAsString(anceResponse);
        } else {
            // Use raw ANCE response as-is
            finalResponse = rawBody;
        }

        // Ensure directory exists
        Path reportsPath = Paths.get(reportsDirectory);
        if (!Files.exists(reportsPath)) {
            Files.createDirectories(reportsPath);
        }

        // Save report to file
        String fileName = "validation_report_" + System.currentTimeMillis() + ".json";
        Path filePath = reportsPath.resolve(fileName);

        try (FileWriter writer = new FileWriter(filePath.toFile())) {
            writer.write(finalResponse);
        } catch (IOException e) {
            throw new RuntimeException("Failed to save validation report", e);
        }

        return filePath.toString();
    }

    /**
     * Sign hash using the user's ANCE SEAL alias (dynamic URL)
     */
    public String signHash(String base64Digest, com.example.unifiedapi.entity.User user) {
        String alias = userCredentialsService.getAnceSealAlias(user);
        String dynamicSignUrl = buildSignUrl(alias);
        return signHashWithPin(base64Digest, pin, dynamicSignUrl);
    }

    public String signHashWithPin(String base64Digest, String userPin, String signUrl) {
        Map<String, Object> payload = new HashMap<>();
        payload.put("password", userPin);
        payload.put("bytes", base64Digest);

        HttpHeaders headers = new HttpHeaders();
        headers.setContentType(MediaType.APPLICATION_JSON);
        HttpEntity<Map<String, Object>> request = new HttpEntity<>(payload, headers);

        ResponseEntity<String> response = restTemplate.postForEntity(signUrl, request, String.class);
        if (response.getStatusCode().is2xxSuccessful()) {
            return response.getBody();
        }
        throw new RuntimeException("ANCE SEAL signing failed: " + response.getStatusCode());
    }
    /**
     * Build the dynamic sign URL using the alias
     */
    public String buildSignUrl(String alias) {
        // signUrlTemplate should contain {alias} as a placeholder
        if (signUrlTemplate.contains("{alias}")) {
            return signUrlTemplate.replace("{alias}", alias);
        }
        // fallback: append alias if not present
        if (!signUrlTemplate.endsWith("/")) {
            return signUrlTemplate + "/" + alias + "/SHA256";
        }
        return signUrlTemplate + alias + "/SHA256";
    }

    @Async
    public CompletableFuture<String> signHashAsync(String base64Digest, User user) {
        return CompletableFuture.supplyAsync(() -> signHash(base64Digest, user));
    }

    @Async
    public CompletableFuture<String> validateSignatureAsync(String xmlContent) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                return validateSignatureWithResponse(xmlContent);
            } catch (Exception e) {
                throw new RuntimeException("Async validation failed", e);
            }
        });
    }

    // Inner class for validation response
    public static class ValidationResponse {
        private boolean success;
        private String message;
        private Object details;

        public ValidationResponse() {}

        public boolean isSuccess() {
            return success;
        }

        public void setSuccess(boolean success) {
            this.success = success;
        }

        public String getMessage() {
            return message;
        }

        public void setMessage(String message) {
            this.message = message;
        }

        public Object getDetails() {
            return details;
        }

        public void setDetails(Object details) {
            this.details = details;
        }
    }
}

package com.example.unifiedapi.service;

import java.time.LocalDateTime;
import java.util.HashMap;
import java.util.Map;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.security.authentication.AuthenticationManager;
import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;
import org.springframework.security.core.Authentication;
import org.springframework.security.core.AuthenticationException;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.example.unifiedapi.dto.auth.AuthResponse;
import com.example.unifiedapi.dto.auth.SignInRequest;
import com.example.unifiedapi.dto.auth.SignUpRequest;
import com.example.unifiedapi.entity.User;
import com.example.unifiedapi.repository.UserRepository;
import com.example.unifiedapi.util.JwtUtil;

@Service
@Transactional
public class AuthService {

    private static final Logger logger = LoggerFactory.getLogger(AuthService.class);

    @Autowired
    private UserRepository userRepository;

    @Autowired
    private PasswordEncoder passwordEncoder;

    @Autowired
    private JwtUtil jwtUtil;

    @Autowired
    private AuthenticationManager authenticationManager;

    @Autowired
    private EncryptionService encryptionService;

    public AuthResponse signIn(SignInRequest request) throws AuthenticationException {
        logger.info("Sign in attempt for user: {}", request.getUsernameOrEmail());

        try {
            // Authenticate user
            Authentication authentication = authenticationManager.authenticate(
                new UsernamePasswordAuthenticationToken(
                    request.getUsernameOrEmail(),
                    request.getPassword()
                )
            );

            User user = (User) authentication.getPrincipal();

            // Update last login
            user.setLastLogin(LocalDateTime.now());
            userRepository.save(user);

            // Generate JWT token with user info
            Map<String, Object> claims = new HashMap<>();
            claims.put("userId", user.getId());
            claims.put("role", user.getRole().name());
            claims.put("email", user.getEmail());

            String token = jwtUtil.generateToken(user, claims);

            logger.info("User {} signed in successfully", user.getUsername());
            return new AuthResponse(token, user);

        } catch (AuthenticationException e) {
            logger.warn("Sign in failed for user: {} - {}", request.getUsernameOrEmail(), e.getMessage());
            throw e;
        }
    }

    public AuthResponse signUp(SignUpRequest request) {
        logger.info("Sign up attempt for user: {}", request.getUsername());

        // Check if username already exists
        if (userRepository.existsByUsername(request.getUsername())) {
            throw new RuntimeException("Ce nom d'utilisateur est d√©j√† utilis√©");
        }

        // Check if email already exists
        if (userRepository.existsByEmail(request.getEmail())) {
            throw new RuntimeException("Cette adresse email est d√©j√† utilis√©e");
        }

        // Create new user
        User user = new User();
        user.setUsername(request.getUsername());
        user.setEmail(request.getEmail());
        user.setPassword(passwordEncoder.encode(request.getPassword()));
        user.setFirstName(request.getFirstName());
        user.setLastName(request.getLastName());
        user.setCompanyName(request.getCompanyName());
        user.setIsVerified(true); // Auto-verify for now

        // Encrypt and store credentials if provided
        if (request.getTtnUsername() != null && !request.getTtnUsername().isEmpty()) {
            user.setTtnUsername(encryptionService.encrypt(request.getTtnUsername()));
        }
        if (request.getTtnPassword() != null && !request.getTtnPassword().isEmpty()) {
            user.setTtnPassword(encryptionService.encrypt(request.getTtnPassword()));
        }
        if (request.getTtnMatriculeFiscal() != null && !request.getTtnMatriculeFiscal().isEmpty()) {
            user.setTtnMatriculeFiscal(encryptionService.encrypt(request.getTtnMatriculeFiscal()));
        }
        if (request.getAnceSealPin() != null && !request.getAnceSealPin().isEmpty()) {
            user.setAnceSealPin(encryptionService.encrypt(request.getAnceSealPin()));
        }
        if (request.getAnceSealAlias() != null && !request.getAnceSealAlias().isEmpty()) {
            user.setAnceSealAlias(encryptionService.encrypt(request.getAnceSealAlias()));
        }
        if (request.getCertificatePath() != null && !request.getCertificatePath().isEmpty()) {
            user.setCertificatePath(request.getCertificatePath());
        }

        user = userRepository.save(user);

        // Generate JWT token
        Map<String, Object> claims = new HashMap<>();
        claims.put("userId", user.getId());
        claims.put("role", user.getRole().name());
        claims.put("email", user.getEmail());

        String token = jwtUtil.generateToken(user, claims);

        logger.info("User {} registered successfully", user.getUsername());
        return new AuthResponse(token, user);
    }

 public User getCurrentUser(String usernameOrEmail) {
    return userRepository.findByUsernameOrEmail(usernameOrEmail, usernameOrEmail)
            .orElseThrow(() -> new RuntimeException("Utilisateur non trouv√©"));
}

}

package com.example.unifiedapi.service;

import java.io.File;
import java.io.IOException;
import java.io.InputStream;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.core.io.ClassPathResource;
import org.springframework.stereotype.Service;

@Service
public class CertificatePathService {

    private static final Logger logger = LoggerFactory.getLogger(CertificatePathService.class);

    @Value("${ance.seal.certificate.default-path:classpath:certificates/icone.cer}")
    private String defaultCertificatePath;

    @Value("${ance.seal.certificate.fallback-path:./certificates/icone.cer}")
    private String fallbackCertificatePath;

    /**
     * Resolves the certificate path, checking user-provided path first, then defaults
     */
    public String resolveCertificatePath(String userProvidedPath) {
        logger.info("Resolving certificate path. User provided: {}", userProvidedPath);

        // 1. If user provided a path, try to use it
        if (userProvidedPath != null && !userProvidedPath.trim().isEmpty() && 
            !userProvidedPath.equals("/path/to/certificate.crt")) {

            String resolvedPath = resolveAndValidatePath(userProvidedPath.trim());
            if (resolvedPath != null) {
                logger.info("Using user-provided certificate path: {}", resolvedPath);
                return resolvedPath;
            }
        }

        // 2. Try default classpath resource
        String resolvedDefault = resolveClasspathResource(defaultCertificatePath);
        if (resolvedDefault != null) {
            logger.info("Using default certificate path: {}", resolvedDefault);
            return resolvedDefault;
        }

        // 2.1. Try auto-detecting certificate with different extensions
        String autoDetected = autoDetectCertificate();
        if (autoDetected != null) {
            logger.info("Auto-detected certificate path: {}", autoDetected);
            return autoDetected;
        }

        // 3. Try fallback file path
        String resolvedFallback = resolveAndValidatePath(fallbackCertificatePath);
        if (resolvedFallback != null) {
            logger.info("Using fallback certificate path: {}", resolvedFallback);
            return resolvedFallback;
        }

        // 4. If nothing works, throw exception with helpful message
        throw new IllegalArgumentException(
            String.format("Certificate not found. Tried:\n" +
                "1. User path: %s\n" +
                "2. Default: %s\n" +
                "3. Fallback: %s\n" +
                "Please place 'icone.crt' in src/main/resources/certificates/ or provide a valid path.",
                userProvidedPath, defaultCertificatePath, fallbackCertificatePath)
        );
    }

    /**
     * Resolves classpath resource and extracts to temp file if needed
     */
    private String resolveClasspathResource(String classpathPath) {
        if (!classpathPath.startsWith("classpath:")) {
            return resolveAndValidatePath(classpathPath);
        }

        try {
            String resourcePath = classpathPath.substring("classpath:".length());
            ClassPathResource resource = new ClassPathResource(resourcePath);

            if (!resource.exists()) {
                logger.debug("Classpath resource not found: {}", classpathPath);
                return null;
            }

            // For classpath resources, we need to extract to a temporary file
            // because the certificate loading requires a file path
            Path tempFile = Files.createTempFile("cert_", ".crt");

            try (InputStream inputStream = resource.getInputStream()) {
                Files.copy(inputStream, tempFile, java.nio.file.StandardCopyOption.REPLACE_EXISTING);
            }

            // Mark for deletion on exit
            tempFile.toFile().deleteOnExit();

            String tempPath = tempFile.toAbsolutePath().toString();
            logger.debug("Extracted classpath certificate to temporary file: {}", tempPath);
            return tempPath;

        } catch (IOException e) {
            logger.error("Failed to extract classpath certificate: {}", e.getMessage());
            return null;
        }
    }

    /**
     * Validates that a file path exists and is readable
     */
    private String resolveAndValidatePath(String path) {
        if (path == null || path.trim().isEmpty()) {
            return null;
        }

        try {
            Path certPath = Paths.get(path);

            if (Files.exists(certPath) && Files.isReadable(certPath)) {
                return certPath.toAbsolutePath().toString();
            } else {
                logger.debug("Certificate file not found or not readable: {}", path);
                return null;
            }
        } catch (Exception e) {
            logger.debug("Invalid certificate path {}: {}", path, e.getMessage());
            return null;
        }
    }

    /**
     * Gets the default certificate path for display in UI
     */
    public String getDefaultCertificatePath() {
        return defaultCertificatePath;
    }

    /**
     * Lists available certificates in the resources directory
     */
    public String[] getAvailableCertificates() {
        try {
            // Try to detect known certificate files instead of listing directory
            // This works better when running from JAR files
            String[] possibleCerts = {
                "icone.crt", "icone.cer", "icone.pem",
                "certificate.crt", "certificate.cer", "certificate.pem",
                "TnTrustQualifiedGovCA.crt", "TunTrustQualifiedCAInter.crt"
            };
            java.util.List<String> foundCerts = new java.util.ArrayList<>();

            for (String certName : possibleCerts) {
                ClassPathResource resource = new ClassPathResource("certificates/" + certName);
                if (resource.exists()) {
                    foundCerts.add(certName);
                }
            }

            return foundCerts.toArray(new String[0]);

        } catch (Exception e) {
            logger.debug("Could not detect certificates: {}", e.getMessage());
        }

        return new String[0];
    }

    /**
     * Gets the paths to the ANCE certificate chain files
     */
    public String[] getAnceCertificateChain() {
        String[] chainFiles = {
            "classpath:certificates/icone.cer",                    // Signing certificate
            "classpath:certificates/TunTrustQualifiedCAInter.crt", // Intermediate
            "classpath:certificates/TnTrustQualifiedGovCA.crt"     // Root
        };

        java.util.List<String> existingChain = new java.util.ArrayList<>();

        for (String chainFile : chainFiles) {
            String resolved = resolveClasspathResource(chainFile);
            if (resolved != null) {
                existingChain.add(resolved);
            }
        }

        return existingChain.toArray(new String[0]);
    }

    /**
     * Auto-detects certificate file with different extensions
     */
    private String autoDetectCertificate() {
        String[] extensions = {".cer", ".crt", ".pem"};
        String[] baseNames = {"icone", "certificate", "cert"};

        for (String baseName : baseNames) {
            for (String ext : extensions) {
                String classpathPath = "classpath:certificates/" + baseName + ext;
                String resolved = resolveClasspathResource(classpathPath);
                if (resolved != null) {
                    logger.debug("Auto-detected certificate: {}", classpathPath);
                    return resolved;
                }
            }
        }

        return null;
    }

    /**
     * Validates certificate path format
     */
    public boolean isValidCertificatePath(String path) {
        if (path == null || path.trim().isEmpty()) {
            return false;
        }

        String trimmedPath = path.trim();

        // Check for placeholder paths
        if (trimmedPath.equals("/path/to/certificate.crt") ||
            trimmedPath.equals("path/to/certificate.crt")) {
            return false;
        }

        // Check file extension
        return trimmedPath.toLowerCase().endsWith(".crt") ||
               trimmedPath.toLowerCase().endsWith(".pem") ||
               trimmedPath.toLowerCase().endsWith(".cer");
    }
}

package com.example.unifiedapi.service;

import com.example.unifiedapi.entity.User;
import com.example.unifiedapi.repository.UserRepository;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.core.userdetails.UserDetailsService;
import org.springframework.security.core.userdetails.UsernameNotFoundException;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

@Service
public class CustomUserDetailsService implements UserDetailsService {

    private static final Logger logger = LoggerFactory.getLogger(CustomUserDetailsService.class);

    @Autowired
    private UserRepository userRepository;

    @Override
    @Transactional
    public UserDetails loadUserByUsername(String usernameOrEmail) throws UsernameNotFoundException {
        logger.debug("Loading user by username or email: {}", usernameOrEmail);

        User user = userRepository.findByUsernameOrEmail(usernameOrEmail, usernameOrEmail)
                .orElseThrow(() -> {
                    logger.warn("User not found with username or email: {}", usernameOrEmail);
                    return new UsernameNotFoundException("Utilisateur non trouv√© avec le nom d'utilisateur ou l'email: " + usernameOrEmail);
                });

        logger.debug("User found: {} (ID: {})", user.getUsername(), user.getId());
        return user;
