package com.example.unifiedapi.service;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Service;

@Service
public class ProgressCleanupService {

    private static final Logger logger = LoggerFactory.getLogger(ProgressCleanupService.class);

    @Autowired
    private ProgressTrackingService progressTrackingService;

    /**
     * Clean up old progress sessions every 5 minutes
     */
    @Scheduled(fixedRate = 300000) // 5 minutes
    public void cleanupOldSessions() {
        try {
            int beforeCount = progressTrackingService.getAllSessions().size();
            progressTrackingService.cleanupOldSessions();
            int afterCount = progressTrackingService.getAllSessions().size();

            if (beforeCount > afterCount) {
                logger.info("Cleaned up {} old progress sessions ({} -> {})", 
                    beforeCount - afterCount, beforeCount, afterCount);
            }
        } catch (Exception e) {
            logger.error("Failed to cleanup old progress sessions: {}", e.getMessage(), e);
        }
    }
}

package com.example.unifiedapi.service;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.stereotype.Service;
import org.springframework.scheduling.annotation.Async;
import org.springframework.context.event.EventListener;

import java.time.LocalDateTime;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.CopyOnWriteArrayList;
import java.util.Map;
import java.util.List;
import java.util.UUID;

@Service
public class ProgressTrackingService {

    private static final Logger logger = LoggerFactory.getLogger(ProgressTrackingService.class);

    // Store progress sessions
    private final Map<String, WorkflowProgress> progressSessions = new ConcurrentHashMap<>();

    // Store progress listeners (for WebSocket connections)
    private final Map<String, List<ProgressListener>> progressListeners = new ConcurrentHashMap<>();

    public interface ProgressListener {
        void onProgressUpdate(WorkflowProgress progress);
    }

    public static class WorkflowProgress {
        private String sessionId;
        private String status; // "INITIALIZING", "PROCESSING", "COMPLETED", "FAILED"
        private String currentStage; // "SIGN", "SAVE", "VALIDATE", "TRANSFORM", "PACKAGE"
        private int overallProgress; // 0-100
        private LocalDateTime lastUpdated;
        private List<FileProgress> files;
        private String message;
        private boolean success;
        private String errorMessage;
        private String zipDownloadUrl;

        public WorkflowProgress(String sessionId) {
            this.sessionId = sessionId;
            this.status = "INITIALIZING";
            this.currentStage = "SIGN";
            this.overallProgress = 0;
            this.lastUpdated = LocalDateTime.now();
            this.files = new CopyOnWriteArrayList<>();
            this.message = "Initialisation du workflow...";
            this.success = false;
        }

        // Getters and setters
        public String getSessionId() { return sessionId; }
        public void setSessionId(String sessionId) { this.sessionId = sessionId; }

        public String getStatus() { return status; }
        public void setStatus(String status) { 
            this.status = status; 
            this.lastUpdated = LocalDateTime.now();
        }

        public String getCurrentStage() { return currentStage; }
        public void setCurrentStage(String currentStage) { 
            this.currentStage = currentStage;
            this.lastUpdated = LocalDateTime.now();
        }

        public int getOverallProgress() { return overallProgress; }
        public void setOverallProgress(int overallProgress) { 
            this.overallProgress = Math.max(0, Math.min(100, overallProgress));
            this.lastUpdated = LocalDateTime.now();
        }

        public LocalDateTime getLastUpdated() { return lastUpdated; }
        public void setLastUpdated(LocalDateTime lastUpdated) { this.lastUpdated = lastUpdated; }

        public List<FileProgress> getFiles() { return files; }
        public void setFiles(List<FileProgress> files) { this.files = files; }

        public String getMessage() { return message; }
        public void setMessage(String message) { 
            this.message = message;
            this.lastUpdated = LocalDateTime.now();
        }

        public boolean isSuccess() { return success; }
        public void setSuccess(boolean success) { this.success = success; }

        public String getErrorMessage() { return errorMessage; }
        public void setErrorMessage(String errorMessage) { this.errorMessage = errorMessage; }

        public String getZipDownloadUrl() { return zipDownloadUrl; }
        public void setZipDownloadUrl(String zipDownloadUrl) { this.zipDownloadUrl = zipDownloadUrl; }
    }

    public static class FileProgress {
        private String filename;
        private String status; // "PENDING", "PROCESSING", "COMPLETED", "FAILED"
        private String stage; // "SIGN", "SAVE", "VALIDATE", "TRANSFORM", "PACKAGE"
        private int progress; // 0-100
        private String errorMessage;
        private String ttnInvoiceId;
        private long fileSize;

        public FileProgress(String filename, long fileSize) {
            this.filename = filename;
            this.fileSize = fileSize;
            this.status = "PENDING";
            this.stage = "SIGN";
            this.progress = 0;
        }

        // Getters and setters
        public String getFilename() { return filename; }
        public void setFilename(String filename) { this.filename = filename; }

        public String getStatus() { return status; }
        public void setStatus(String status) { this.status = status; }

        public String getStage() { return stage; }
        public void setStage(String stage) { this.stage = stage; }

        public int getProgress() { return progress; }
        public void setProgress(int progress) { this.progress = Math.max(0, Math.min(100, progress)); }

        public String getErrorMessage() { return errorMessage; }
        public void setErrorMessage(String errorMessage) { this.errorMessage = errorMessage; }

        public String getTtnInvoiceId() { return ttnInvoiceId; }
        public void setTtnInvoiceId(String ttnInvoiceId) { this.ttnInvoiceId = ttnInvoiceId; }

        public long getFileSize() { return fileSize; }
        public void setFileSize(long fileSize) { this.fileSize = fileSize; }
    }

    /**
     * Create a new progress session
     */
    public String createProgressSession(List<String> filenames, List<Long> fileSizes) {
        String sessionId = UUID.randomUUID().toString();
        WorkflowProgress progress = new WorkflowProgress(sessionId);

        // Initialize file progress
        for (int i = 0; i < filenames.size(); i++) {
            String filename = filenames.get(i);
            long fileSize = i < fileSizes.size() ? fileSizes.get(i) : 0L;
            progress.getFiles().add(new FileProgress(filename, fileSize));
        }

        progressSessions.put(sessionId, progress);
        logger.info("Created progress session: {} with {} files", sessionId, filenames.size());

        return sessionId;
    }

    /**
     * Get progress for a session
     */
    public WorkflowProgress getProgress(String sessionId) {
        return progressSessions.get(sessionId);
    }

    /**
     * Update overall workflow progress
     */
    public void updateWorkflowProgress(String sessionId, String status, String stage, int progress, String message) {
        WorkflowProgress workflowProgress = progressSessions.get(sessionId);
        if (workflowProgress != null) {
            workflowProgress.setStatus(status);
            workflowProgress.setCurrentStage(stage);
            workflowProgress.setOverallProgress(progress);
            workflowProgress.setMessage(message);

            logger.debug("Updated workflow progress for session {}: {}% - {}", sessionId, progress, message);
            notifyListeners(sessionId, workflowProgress);
        }
    }

    /**
     * Update file-specific progress
     */
    public void updateFileProgress(String sessionId, String filename, String status, String stage, int progress, String errorMessage) {
        WorkflowProgress workflowProgress = progressSessions.get(sessionId);
        if (workflowProgress != null) {
            FileProgress fileProgress = workflowProgress.getFiles().stream()
                .filter(fp -> fp.getFilename().equals(filename))
                .findFirst()
                .orElse(null);

            if (fileProgress != null) {
                fileProgress.setStatus(status);
                fileProgress.setStage(stage);
                fileProgress.setProgress(progress);
                if (errorMessage != null) {
                    fileProgress.setErrorMessage(errorMessage);
                }

                // Update overall progress based on file progress
                updateOverallProgress(sessionId);

                logger.debug("Updated file progress for {}: {}% - {} ({})", filename, progress, stage, status);
                notifyListeners(sessionId, workflowProgress);
            }
        }
    }

    /**
     * Set TTN Invoice ID for a file
     */
    public void setTtnInvoiceId(String sessionId, String filename, String ttnInvoiceId) {
        WorkflowProgress workflowProgress = progressSessions.get(sessionId);
        if (workflowProgress != null) {
            FileProgress fileProgress = workflowProgress.getFiles().stream()
                .filter(fp -> fp.getFilename().equals(filename))
                .findFirst()
                .orElse(null);

            if (fileProgress != null) {
                fileProgress.setTtnInvoiceId(ttnInvoiceId);
                logger.info("üéØ Set TTN Invoice ID for {}: {}", filename, ttnInvoiceId);
                notifyListeners(sessionId, workflowProgress);
            } else {
                logger.warn("‚ö†Ô∏è Could not find file progress for {} to set TTN ID: {}", filename, ttnInvoiceId);
            }
        }
    }

    /**
     * Complete workflow processing
     */
    public void completeWorkflow(String sessionId, boolean success, String message) {
        WorkflowProgress workflowProgress = progressSessions.get(sessionId);
        if (workflowProgress != null) {
            workflowProgress.setStatus(success ? "COMPLETED" : "FAILED");
            workflowProgress.setSuccess(success);
            workflowProgress.setMessage(message);
            workflowProgress.setOverallProgress(100);

            if (!success) {
                workflowProgress.setErrorMessage(message);
            }

            logger.info("Completed workflow for session {}: {} - {}", sessionId, success ? "SUCCESS" : "FAILED", message);
            notifyListeners(sessionId, workflowProgress);
        }
    }

    /**
     * Set ZIP download URL for a workflow session
     */
    public void setZipDownloadUrl(String sessionId, String zipDownloadUrl) {
        WorkflowProgress workflowProgress = progressSessions.get(sessionId);
        if (workflowProgress != null) {
            workflowProgress.setZipDownloadUrl(zipDownloadUrl);
            logger.debug("Set ZIP download URL for session {}: {}", sessionId, zipDownloadUrl);
            notifyListeners(sessionId, workflowProgress);
        }
    }

    /**
     * Calculate and update overall progress based on file progress
     */
    private void updateOverallProgress(String sessionId) {
        WorkflowProgress workflowProgress = progressSessions.get(sessionId);
        if (workflowProgress != null && !workflowProgress.getFiles().isEmpty()) {
            int totalProgress = workflowProgress.getFiles().stream()
                .mapToInt(FileProgress::getProgress)
                .sum();
            int averageProgress = totalProgress / workflowProgress.getFiles().size();
            workflowProgress.setOverallProgress(averageProgress);
        }
    }

    /**
     * Add progress listener for real-time updates
     */
    public void addProgressListener(String sessionId, ProgressListener listener) {
        progressListeners.computeIfAbsent(sessionId, k -> new CopyOnWriteArrayList<>()).add(listener);
        logger.debug("Added progress listener for session: {}", sessionId);
    }

    /**
     * Remove progress listener
     */
    public void removeProgressListener(String sessionId, ProgressListener listener) {
        List<ProgressListener> listeners = progressListeners.get(sessionId);
        if (listeners != null) {
            listeners.remove(listener);
            if (listeners.isEmpty()) {
                progressListeners.remove(sessionId);
            }
        }
        logger.debug("Removed progress listener for session: {}", sessionId);
    }

    /**
     * Notify all listeners of progress update
     */
    @Async
    private void notifyListeners(String sessionId, WorkflowProgress progress) {
        List<ProgressListener> listeners = progressListeners.get(sessionId);
        if (listeners != null) {
            for (ProgressListener listener : listeners) {
                try {
                    listener.onProgressUpdate(progress);
                } catch (Exception e) {
                    logger.warn("Failed to notify progress listener for session {}: {}", sessionId, e.getMessage());
                }
            }
        }
    }

    /**
     * Clean up old progress sessions
     */
    public void cleanupOldSessions() {
        LocalDateTime cutoff = LocalDateTime.now().minusMinutes(30);
        progressSessions.entrySet().removeIf(entry -> 
            entry.getValue().getLastUpdated().isBefore(cutoff));
        logger.debug("Cleaned up old progress sessions");
    }

    /**
     * Get all active sessions (for debugging)
     */
    public Map<String, WorkflowProgress> getAllSessions() {
        return new ConcurrentHashMap<>(progressSessions);
    }
}

package com.example.unifiedapi.service;

import com.example.unifiedapi.dto.WorkflowResponse;
import com.example.unifiedapi.entity.User;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import org.springframework.web.multipart.MultipartFile;

import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.nio.file.Path;
import java.util.Map;

/**
 * Service responsible for collecting processing results, creating ZIP files,
 * and finalizing workflow responses.
 */
@Service
public class ResultCollectionService {

    private static final Logger logger = LoggerFactory.getLogger(ResultCollectionService.class);

    private final ZipService zipService;
    private final ProgressTrackingService progressTrackingService;
    private final UserCredentialsService userCredentialsService;
    private final OperationLogService operationLogService;

    @Autowired
    public ResultCollectionService(ZipService zipService,
                                 ProgressTrackingService progressTrackingService,
                                 UserCredentialsService userCredentialsService,
                                 OperationLogService operationLogService) {
        this.zipService = zipService;
        this.progressTrackingService = progressTrackingService;
        this.userCredentialsService = userCredentialsService;
        this.operationLogService = operationLogService;
    }

    /**
     * Finalize workflow response by creating ZIP file and setting final status
     */
    public WorkflowResponse finalizeWorkflowResponse(WorkflowResponse response,
                                                   Map<String, String> signedXmlFiles,
                                                   Map<String, String> validationReports,
                                                   Map<String, String> htmlFiles,
                                                   Map<String, String> errorReports,
                                                   int successfulFiles, int failedFiles, int totalFiles,
                                                   String sessionId, String baseUrl, User currentUser) {

        logger.info("üì¶ Finalizing workflow response - Success: {}, Failed: {}, Total: {}", 
                   successfulFiles, failedFiles, totalFiles);

        try {
            // Create ZIP file with all results
            createResultsZipFile(response, signedXmlFiles, validationReports, htmlFiles, errorReports,
                               sessionId, baseUrl, currentUser);

            // Set final response status and message
            setFinalResponseStatus(response, successfulFiles, failedFiles, totalFiles);

            // Complete workflow progress tracking
            completeWorkflowProgress(sessionId, successfulFiles, failedFiles, totalFiles);

            // Log final workflow status
            logFinalWorkflowStatus(currentUser, successfulFiles, failedFiles, totalFiles);

        } catch (Exception e) {
            logger.error("‚ùå Failed to finalize workflow response: {}", e.getMessage(), e);
            response.setSuccess(false);
            response.setMessage("Traitement termin√© mais √©chec de finalisation: " + e.getMessage());
            progressTrackingService.completeWorkflow(sessionId, false, 
                "Erreur de finalisation: " + e.getMessage());
        }

        return response;
    }

    /**
     * Create ZIP file with all processing results
     */
    private void createResultsZipFile(WorkflowResponse response,
                                    Map<String, String> signedXmlFiles,
                                    Map<String, String> validationReports,
                                    Map<String, String> htmlFiles,
                                    Map<String, String> errorReports,
                                    String sessionId, String baseUrl, User currentUser) throws Exception {

        logger.info("üì¶ Creating ZIP file with {} signed files, {} validation reports, {} HTML files, {} error reports",
                   signedXmlFiles.size(), validationReports.size(), htmlFiles.size(), errorReports.size());

        progressTrackingService.updateWorkflowProgress(sessionId, "PROCESSING", "PACKAGE", 95,
            "Cr√©ation de l'archive ZIP...");

        String ttnUsername = userCredentialsService.getTtnUsername(currentUser);
        // Build map of filename to TTN invoice ID from workflow results
        Map<String, String> ttnInvoiceIds = new java.util.HashMap<>();
        if (response.getResults() != null) {
            for (WorkflowResponse.FileProcessingResult result : response.getResults()) {
                if (result.getTtnInvoiceId() != null && !result.getTtnInvoiceId().isEmpty()) {
                    ttnInvoiceIds.put(result.getFilename(), result.getTtnInvoiceId());
                }
            }
        }
        Path zipPath = zipService.createProcessedFilesZipWithErrors(
            signedXmlFiles, validationReports, htmlFiles, errorReports, ttnInvoiceIds, ttnUsername
        );

        String downloadUrl = zipService.getDownloadUrl(zipPath, baseUrl);
        response.setZipDownloadUrl(downloadUrl);

        // Set ZIP download URL in progress tracking
        progressTrackingService.setZipDownloadUrl(sessionId, downloadUrl);

        logger.info("‚úÖ ZIP file created successfully: {}", downloadUrl);
    }

    /**
     * Set final response status and message based on processing results
     */
    private void setFinalResponseStatus(WorkflowResponse response, int successfulFiles, 
                                      int failedFiles, int totalFiles) {

        if (successfulFiles > 0) {
            response.setSuccess(true);
            if (failedFiles > 0) {
                response.setMessage(String.format(
                    "Traitement parall√®le termin√© avec succ√®s partiel. %d/%d fichiers r√©ussis, %d fichiers √©chou√©s. Archive ZIP cr√©√©e avec tous les r√©sultats.",
                    successfulFiles, totalFiles, failedFiles
                ));
            } else {
                response.setMessage(String.format(
                    "Traitement parall√®le termin√© avec succ√®s complet. %d/%d fichiers trait√©s avec succ√®s. Archive ZIP cr√©√©e.",
                    successfulFiles, totalFiles
                ));
            }
        } else {
            response.setSuccess(false);
            response.setMessage(String.format(
                "Traitement parall√®le termin√© avec √©chec. %d/%d fichiers √©chou√©s. Archive ZIP cr√©√©e avec les rapports d'erreur.",
                failedFiles, totalFiles
            ));
        }

        logger.info("üìä Final status - Success: {}, Message: {}", response.isSuccess(), response.getMessage());
    }

    /**
     * Complete workflow progress tracking
     */
    private void completeWorkflowProgress(String sessionId, int successfulFiles, 
                                        int failedFiles, int totalFiles) {

        boolean overallSuccess = successfulFiles > 0;
        String completionMessage;

        if (overallSuccess) {
            if (failedFiles > 0) {
                completionMessage = String.format("Traitement parall√®le termin√© avec succ√®s partiel: %d/%d fichiers r√©ussis", 
                                                 successfulFiles, totalFiles);
            } else {
                completionMessage = String.format("Traitement parall√®le termin√© avec succ√®s complet: %d/%d fichiers trait√©s", 
                                                 successfulFiles, totalFiles);
            }
        } else {
            completionMessage = String.format("Traitement parall√®le termin√© avec √©chec: %d fichiers √©chou√©s", failedFiles);
        }

        progressTrackingService.completeWorkflow(sessionId, overallSuccess, completionMessage);
        logger.info("üìà Workflow progress completed - Session: {}, Success: {}", sessionId, overallSuccess);
    }

    /**
     * Log final workflow status for audit purposes
     */
    private void logFinalWorkflowStatus(User currentUser, int successfulFiles, 
                                      int failedFiles, int totalFiles) {
        try {
            String ttnUsername = userCredentialsService.getTtnUsername(currentUser);
            String ttnMatriculeFiscal = userCredentialsService.getTtnMatriculeFiscal(currentUser);

            String operationType = "PARALLEL_WORKFLOW_COMPLETE";
            String status = successfulFiles > 0 ? "SUCCESS" : "FAILURE";
            String details = String.format("Parallel workflow completed: %d successful, %d failed, %d total. ZIP created.", 
                                          successfulFiles, failedFiles, totalFiles);

            operationLogService.logOperationWithDetails(
                operationType, status, null, null, details,
                ttnUsername, ttnMatriculeFiscal, null
            );

            logger.info("üìù Workflow completion logged - Type: {}, Status: {}", operationType, status);

        } catch (Exception logError) {
            logger.warn("Failed to log final workflow status: {}", logError.getMessage());
        }
    }



    /**
     * Generate summary statistics for the workflow
     */
    public String generateWorkflowSummary(int successfulFiles, int failedFiles, int totalFiles) {
        double successRate = totalFiles > 0 ? (double) successfulFiles / totalFiles * 100 : 0;

        return String.format(
            "Workflow Summary: %d/%d files processed successfully (%.1f%% success rate), %d failed",
            successfulFiles, totalFiles, successRate, failedFiles
        );
    }
}

package com.example.unifiedapi.service;

import com.example.unifiedapi.dto.TtnConsultEfactRequest;
import com.example.unifiedapi.dto.TtnConsultEfactResponse;
import com.example.unifiedapi.dto.TtnConsultHtmlRequest;
import com.example.unifiedapi.dto.TtnConsultHtmlResponse;
import com.example.unifiedapi.entity.User;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.xpath.XPath;
import javax.xml.xpath.XPathConstants;
import javax.xml.xpath.XPathFactory;
import org.w3c.dom.Document;

import java.io.ByteArrayInputStream;
import java.util.List;
import java.util.Map;

/**
 * Service for TTN consultation with HTML transformation
 */
@Service
public class TtnConsultService {

    private static final Logger logger = LoggerFactory.getLogger(TtnConsultService.class);

    private final TtnOperationsService ttnOperationsService;
    private final TtnTransformationService ttnTransformationService;
    private final UserCredentialsService userCredentialsService;
    private final OperationLogService operationLogService;

    @Autowired
    public TtnConsultService(TtnOperationsService ttnOperationsService,
                            TtnTransformationService ttnTransformationService,
                            UserCredentialsService userCredentialsService,
                            OperationLogService operationLogService) {
        this.ttnOperationsService = ttnOperationsService;
        this.ttnTransformationService = ttnTransformationService;
        this.userCredentialsService = userCredentialsService;
        this.operationLogService = operationLogService;
    }

    /**
     * Consult TTN e-facturation system and transform results to HTML
     */
    public TtnConsultHtmlResponse consultWithHtml(TtnConsultHtmlRequest request) {


        TtnConsultHtmlResponse response = new TtnConsultHtmlResponse();

        try {
            // Get user credentials
            String ttnUsername = userCredentialsService.getTtnUsername();
            String ttnPassword = userCredentialsService.getTtnPassword();
            String ttnMatriculeFiscal = userCredentialsService.getTtnMatriculeFiscal();

            // Create TTN consult request
            TtnConsultEfactRequest ttnRequest = new TtnConsultEfactRequest(
                ttnUsername, ttnPassword, ttnMatriculeFiscal, request.getCriteria()
            );

            // Perform consultation
            logger.info("Consulting TTN e-facturation system...");
            TtnConsultEfactResponse ttnResponse = ttnOperationsService.consultEfact(ttnRequest);

            if (!ttnResponse.isSuccess()) {
                logger.warn("TTN consultation failed: {}", ttnResponse.getError());
                response.setSuccess(false);
                response.setError(ttnResponse.getError());
                response.setRawResponse(ttnResponse.getRawResponse());
                return response;
            }

            // Copy basic response data
            response.setSuccess(true);
            response.setCount(ttnResponse.getCount());
            response.setInvoices(ttnResponse.getInvoices());
            response.setRawResponse(ttnResponse.getRawResponse());

            // Transform to HTML if invoices found and XML content is available
            if (ttnResponse.getCount() > 0 && shouldTransformToHtml(request)) {
                logger.info("Attempting HTML transformation for {} invoices", ttnResponse.getCount());

                try {
                    String htmlContent = transformInvoicesToHtml(
                        ttnResponse, ttnUsername, ttnPassword, ttnMatriculeFiscal, request
                    );
                    response.setHtmlContent(htmlContent);
                    logger.info("HTML transformation completed successfully");
                } catch (Exception htmlError) {
                    logger.warn("HTML transformation failed, continuing without HTML: {}", htmlError.getMessage());
                    response.setHtmlTransformationError("HTML transformation failed: " + htmlError.getMessage());
                }
            }

            // Log successful operation
            operationLogService.logOperationWithDetails(
                "TTN_CONSULT_HTML", "SUCCESS", null, null,
                "TTN consultation with HTML completed successfully",
                ttnUsername, ttnMatriculeFiscal, null
            );

            logger.info("TTN consultation with HTML completed successfully - {} invoices found", 
                       response.getCount());

        } catch (Exception e) {
            logger.error("Error in TTN consultation with HTML: {}", e.getMessage(), e);

            response.setSuccess(false);
            response.setError("Consultation failed: " + e.getMessage());

            // Log failed operation
            try {
                String ttnUsername = userCredentialsService.getTtnUsername();
                String ttnMatriculeFiscal = userCredentialsService.getTtnMatriculeFiscal();

                operationLogService.logOperationWithDetails(
                    "TTN_CONSULT_HTML", "FAILURE", null, null,
                    "TTN consultation with HTML failed",
                    ttnUsername, ttnMatriculeFiscal, e.getMessage()
                );
            } catch (Exception logError) {
                logger.warn("Failed to log operation details: {}", logError.getMessage());
            }
        }

        return response;
    }

    /**
     * Determine if HTML transformation should be attempted
     */
    private boolean shouldTransformToHtml(TtnConsultHtmlRequest request) {
        // Transform to HTML if specific invoice ID is requested (single invoice scenario)
        if (request.getCriteria() instanceof Map) {
            Map<?, ?> criteria = (Map<?, ?>) request.getCriteria();
            return criteria.containsKey("idSaveEfact") && 
                   criteria.get("idSaveEfact") != null && 
                   !criteria.get("idSaveEfact").toString().trim().isEmpty();
        }
        return false;
    }

    /**
     * Transform invoice data to HTML using TTN consultation and transformation services
     */
    private String transformInvoicesToHtml(TtnConsultEfactResponse ttnResponse, 
                                          String ttnUsername, String ttnPassword, 
                                          String ttnMatriculeFiscal, TtnConsultHtmlRequest request) 
                                          throws Exception {

        // Extract idSaveEfact from criteria for individual invoice consultation
        String idSaveEfact = null;
        if (request.getCriteria() instanceof Map) {
            Map<?, ?> criteria = (Map<?, ?>) request.getCriteria();
            Object id = criteria.get("idSaveEfact");
            if (id != null) {
                idSaveEfact = id.toString().trim();
            }
        }

        if (idSaveEfact == null || idSaveEfact.isEmpty()) {
            throw new RuntimeException("idSaveEfact is required for HTML transformation");
        }

        logger.info("Transforming invoice with ID: {} to HTML", idSaveEfact);

        // Create specific request for the invoice ID to get XML content
        TtnConsultEfactRequest specificRequest = new TtnConsultEfactRequest(
            ttnUsername, ttnPassword, ttnMatriculeFiscal, idSaveEfact
        );

        // Retry mechanism to get XML content (similar to InvoiceFileProcessor)
        String base64XmlContent = null;


            try {
                TtnConsultEfactResponse specificResponse = ttnOperationsService.consultEfact(specificRequest);

                if (specificResponse.isSuccess()) {
                    String rawResponse = specificResponse.getRawResponse();
                    base64XmlContent = extractXmlContentFromResponse(rawResponse);

                    if (base64XmlContent != null && !base64XmlContent.trim().isEmpty()) {
                        logger.info("Successfully extracted XML content for invoice: {}", idSaveEfact);

                    }
                }
            } catch (Exception e) {
                logger.error("Error extracting xmlContent: {}", e.getMessage(), e);
            }






        // Transform to HTML using the transformation service
        String htmlContent = ttnTransformationService.transformXmlToHtml(
            base64XmlContent, ttnUsername, ttnPassword, ttnMatriculeFiscal, 
            "invoice_" + idSaveEfact + ".xml"
        );

        logger.info("HTML transformation completed for invoice: {}", idSaveEfact);
        return htmlContent;
    }

    /**
     * Extract XML content from TTN SOAP response
     */
    private String extractXmlContentFromResponse(String rawResponse) {
        try {
            DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
            factory.setNamespaceAware(true);
            DocumentBuilder builder = factory.newDocumentBuilder();
            Document doc = builder.parse(new ByteArrayInputStream(rawResponse.getBytes()));
            XPath xpath = XPathFactory.newInstance().newXPath();
            String expression = "//*[local-name()='xmlContent']";
            String xmlContent = (String) xpath.evaluate(expression, doc, XPathConstants.STRING);

            return (xmlContent != null && !xmlContent.trim().isEmpty()) ? xmlContent.trim() : null;
        } catch (Exception e) {
            logger.warn("Failed to extract XML content from response: {}", e.getMessage());
            return null;
        }
    }
}

package com.example.unifiedapi.service;

import com.example.unifiedapi.dto.TtnSaveEfactRequest;
import com.example.unifiedapi.entity.User;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import org.springframework.web.multipart.MultipartFile;

import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.util.Arrays;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

/**
 * Service dedicated to TTN (Tunisie TradeNet) integration operations.
 * Handles saving invoices to TTN and extracting invoice IDs.
 */
@Service
public class TtnIntegrationService {

    private static final Logger logger = LoggerFactory.getLogger(TtnIntegrationService.class);

    private final TtnOperationsService ttnOperationsService;
    private final UserCredentialsService userCredentialsService;

    @Autowired
    public TtnIntegrationService(TtnOperationsService ttnOperationsService,
                               UserCredentialsService userCredentialsService) {
        this.ttnOperationsService = ttnOperationsService;
        this.userCredentialsService = userCredentialsService;
    }

    /**
     * Save a single signed XML file to TTN and extract the invoice ID
     */
    public String saveSingleFileToTtn(String signedXml, String filename, User currentUser) throws Exception {
        logger.info("üíæ Saving file to TTN: {}", filename);

        // Create a temporary MultipartFile from the signed XML
        MultipartFile tempFile = createTempMultipartFile(signedXml, filename);

        // Get user credentials from passed user
        String ttnUsername = userCredentialsService.getTtnUsername(currentUser);
        String ttnPassword = userCredentialsService.getTtnPassword(currentUser);
        String ttnMatriculeFiscal = userCredentialsService.getTtnMatriculeFiscal(currentUser);

        TtnSaveEfactRequest ttnRequest = new TtnSaveEfactRequest(
            ttnUsername, ttnPassword, ttnMatriculeFiscal
        );

        var saveResponse = ttnOperationsService.saveEfact(ttnRequest, Arrays.asList(tempFile));

        if (saveResponse.isSuccess() && saveResponse.getSuccessCount() > 0) {
            var fileResult = saveResponse.getResults().get(0);
            if (fileResult.isSuccess()) {
                String invoiceId = extractInvoiceIdFromResponse(fileResult.getReference());
                logger.info("‚úÖ TTN save successful for file: {} with ID: {}", filename, invoiceId);
                return invoiceId;
            } else {
                throw new RuntimeException("TTN save failed: " + fileResult.getError());
            }
        } else {
            throw new RuntimeException("TTN save operation failed");
        }
    }

    /**
     * Extract invoice ID from TTN response
     */
    public String extractInvoiceIdFromResponse(String response) {
        // Extract ID from response like "Facture enregistree avec ID 1805137 est en cours de validation"
        Pattern pattern = Pattern.compile("ID\\s+(\\d+)");
        Matcher matcher = pattern.matcher(response);

        if (matcher.find()) {
            String invoiceId = matcher.group(1);
            logger.debug("Extracted invoice ID: {} from response: {}", invoiceId, response);
            return invoiceId;
        }

        // If no pattern match, return the full response
        logger.warn("Could not extract invoice ID from response: {}", response);
        return response;
    }

    /**
     * Validate TTN credentials for a user
     */
    public void validateTtnCredentials(User currentUser) throws Exception {
        String ttnUsername = userCredentialsService.getTtnUsername(currentUser);
        String ttnPassword = userCredentialsService.getTtnPassword(currentUser);
        String ttnMatriculeFiscal = userCredentialsService.getTtnMatriculeFiscal(currentUser);

        if (ttnUsername == null || ttnUsername.trim().isEmpty()) {
            throw new IllegalArgumentException("TTN username is required");
        }
        if (ttnPassword == null || ttnPassword.trim().isEmpty()) {
            throw new IllegalArgumentException("TTN password is required");
        }
        if (ttnMatriculeFiscal == null || ttnMatriculeFiscal.trim().isEmpty()) {
            throw new IllegalArgumentException("TTN matricule fiscal is required");
        }

        logger.info("‚úÖ TTN credentials validated for user: {}", currentUser.getUsername());
    }

    /**
     * Create temporary MultipartFile from signed XML content
     */
    private MultipartFile createTempMultipartFile(String signedXml, String filename) {
        byte[] xmlBytes = signedXml.getBytes(java.nio.charset.StandardCharsets.UTF_8);

        return new MultipartFile() {
            @Override
            public String getName() {
                return "file";
            }

            @Override
            public String getOriginalFilename() {
                return filename;
            }

            @Override
            public String getContentType() {
                return "application/xml";
            }

            @Override
            public boolean isEmpty() {
                return xmlBytes.length == 0;
            }

            @Override
            public long getSize() {
                return xmlBytes.length;
            }

            @Override
            public byte[] getBytes() {
                return xmlBytes;
            }

            @Override
            public InputStream getInputStream() {
                return new ByteArrayInputStream(xmlBytes);
            }

            @Override
            public void transferTo(File dest) throws IOException, IllegalStateException {
                try (FileOutputStream fos = new FileOutputStream(dest)) {
                    fos.write(xmlBytes);
                }
            }
        };
    }

    /**
     * Check if TTN service is available
     */
    public boolean isTtnServiceAvailable() {
        try {
            // Simple health check - could be enhanced with actual TTN ping
            return ttnOperationsService != null;
        } catch (Exception e) {
            logger.warn("TTN service availability check failed: {}", e.getMessage());
            return false;
        }
    }

    /**
     * Get TTN service status information
     */
    public String getTtnServiceStatus() {
        if (isTtnServiceAvailable()) {
            return "TTN service is available";
        } else {
            return "TTN service is not available";
        }
    }
}

package com.example.unifiedapi.service;

import com.example.unifiedapi.dto.TtnSaveEfactRequest;
import com.example.unifiedapi.dto.TtnConsultEfactRequest;
import com.example.unifiedapi.dto.TtnSaveEfactResponse;
import com.example.unifiedapi.dto.TtnConsultEfactResponse;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.http.*;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;
import org.springframework.web.multipart.MultipartFile;

import java.util.ArrayList;
import java.util.Base64;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

@Service
public class TtnOperationsService {

    private static final Logger logger = LoggerFactory.getLogger(TtnOperationsService.class);

    @Value("${ttn.soap.url}")
    private String soapUrl;

    private final RestTemplate restTemplate;
    private final OperationLogService operationLogService;

    @Autowired
    public TtnOperationsService(RestTemplate restTemplate, OperationLogService operationLogService) {
        this.restTemplate = restTemplate;
        this.operationLogService = operationLogService;
    }

    public TtnSaveEfactResponse saveEfact(TtnSaveEfactRequest request, List<MultipartFile> files) {
        logger.info("Processing saveEfact request for user: {}, matricule: {}, files: {}", 
                   request.getUsername(), request.getMatriculeFiscal(), files.size());

        TtnSaveEfactResponse response = new TtnSaveEfactResponse();
        response.setSuccess(true);
        response.setTotalProcessed(files.size());
        response.setResults(new ArrayList<>());

        int successCount = 0;
        int errorCount = 0;

        for (MultipartFile file : files) {
            TtnSaveEfactResponse.FileResult fileResult = new TtnSaveEfactResponse.FileResult();
            fileResult.setFilename(file.getOriginalFilename());

            try {
                logger.info("Processing file: {}", file.getOriginalFilename());

                // Convert file to base64
                String base64Content = Base64.getEncoder().encodeToString(file.getBytes());

                // Create SOAP request
                String soapRequest = createSaveEfactSoapRequest(
                    request.getUsername(), 
                    request.getPassword(), 
                    request.getMatriculeFiscal(), 
                    base64Content
                );

                // Send SOAP request
                String soapResponse = sendSoapRequest(soapRequest);
                logger.debug("SOAP response for file {}: {}", file.getOriginalFilename(), soapResponse);

                // Parse response
                String reference = extractReferenceFromResponse(soapResponse);

                fileResult.setSuccess(true);
                fileResult.setReference(reference);
                fileResult.setRawResponse(soapResponse);
                successCount++;

                // Log successful operation
                operationLogService.logOperationWithDetails(
                    "TTN_SAVE", "SUCCESS", file.getSize(), file.getOriginalFilename(),
                    "File processed successfully", request.getUsername(), 
                    request.getMatriculeFiscal(), null
                );

                logger.info("File {} processed successfully with reference: {}", 
                           file.getOriginalFilename(), reference);

            } catch (Exception e) {
                logger.error("Error processing file {}: {}", file.getOriginalFilename(), e.getMessage(), e);

                fileResult.setSuccess(false);
                fileResult.setError(e.getMessage());
                errorCount++;

                // Log failed operation
                operationLogService.logOperationWithDetails(
                    "TTN_SAVE", "FAILURE", file.getSize(), file.getOriginalFilename(),
                    "File processing failed", request.getUsername(), 
                    request.getMatriculeFiscal(), e.getMessage()
                );
            }

            response.getResults().add(fileResult);
        }

        response.setSuccessCount(successCount);
        response.setErrorCount(errorCount);

        logger.info("SaveEfact completed. Total: {}, Success: {}, Errors: {}", 
                   files.size(), successCount, errorCount);

        return response;
    }

    public TtnConsultEfactResponse consultEfact(TtnConsultEfactRequest request) {
        logger.info("Processing consultEfact request for user: {}, matricule: {}", 
                   request.getUsername(), request.getMatriculeFiscal());

        try {
            // Create SOAP request
            String soapRequest = createConsultEfactSoapRequest(
                request.getUsername(), 
                request.getPassword(), 
                request.getMatriculeFiscal(), 
                request.getCriteria()
            );
            logger.info("idSaveEfact criteria: {}", request.getCriteria());
            // Wait 5 seconds before sending the SOAP request
            try {
                Thread.sleep(5000);
            } catch (InterruptedException ie) {
                Thread.currentThread().interrupt();
                logger.warn("Interrupted while waiting before consultEfact request");
            }
            // Send SOAP request
            String soapResponse = sendSoapRequest(soapRequest);
            logger.debug("SOAP response for consultEfact: {}", soapResponse);

            // Parse response
            TtnConsultEfactResponse response = parseConsultEfactResponse(soapResponse);
            response.setRawResponse(soapResponse);

            // Log successful operation
            operationLogService.logOperationWithDetails(
                "TTN_CONSULT", "SUCCESS", null, null,
                "Consult operation completed", request.getUsername(), 
                request.getMatriculeFiscal(), null
            );

            logger.info("ConsultEfact completed successfully");
            return response;

        } catch (Exception e) {
            logger.error("Error in consultEfact: {}", e.getMessage(), e);

            // Log failed operation
            operationLogService.logOperationWithDetails(
                "TTN_CONSULT", "FAILURE", null, null,
                "Consult operation failed", request.getUsername(), 
                request.getMatriculeFiscal(), e.getMessage()
            );

            TtnConsultEfactResponse response = new TtnConsultEfactResponse();
            response.setSuccess(false);
            response.setError(e.getMessage());
            return response;
        }
    }

    private String createSaveEfactSoapRequest(String username, String password, String matriculeFiscal, String base64Content) {
        return String.format(
            "<?xml version=\"1.0\" encoding=\"UTF-8\"?>" +
            "<soap:Envelope xmlns:soap=\"http://schemas.xmlsoap.org/soap/envelope/\" " +
            "xmlns:ser=\"http://services.elfatoura.tradenet.com.tn/\">" +
            "<soap:Header/>" +
            "<soap:Body>" +
            "<ser:saveEfact>" +
            "<arg0>%s</arg0>" +
            "<arg1>%s</arg1>" +
            "<arg2>%s</arg2>" +
            "<arg3>%s</arg3>" +
            "</ser:saveEfact>" +
            "</soap:Body>" +
            "</soap:Envelope>",
            username, password, matriculeFiscal, base64Content
        );
    }

    private String createConsultEfactSoapRequest(String username, String password, String matriculeFiscal, Object criteria) {
        // Serialize criteria to proper XML format
        String criteriaXml = serializeCriteriaToXml(criteria);

        logger.debug("Generated criteria XML: {}", criteriaXml);

        return String.format(
            "<?xml version=\"1.0\" encoding=\"UTF-8\"?>" +
            "<soapenv:Envelope xmlns:soapenv=\"http://schemas.xmlsoap.org/soap/envelope/\" " +
            "xmlns:ser=\"http://services.elfatoura.tradenet.com.tn/\">" +
            "<soapenv:Header/>" +
            "<soapenv:Body>" +
            "<ser:consultEfact>" +
            "<!--Optional:-->" +
            "<arg0>%s</arg0>" +
            "<!--Optional:-->" +
            "<arg1>%s</arg1>" +
            "<!--Optional:-->" +
            "<arg2>%s</arg2>" +
            "<arg3>%s</arg3>" +
            "</ser:consultEfact>" +
            "</soapenv:Body>" +
            "</soapenv:Envelope>",
            username, password, matriculeFiscal, criteriaXml
        );
    }

    /**
     * Serialize criteria object to XML format expected by TTN service
     * Based on real SOAP UI format: criteria elements directly inside <arg3>
     */
    private String serializeCriteriaToXml(Object criteria) {
        if (criteria == null) {
            // Return empty string for no criteria
            return "";
        }

        StringBuilder xmlBuilder = new StringBuilder();

        try {
            if (criteria instanceof Map) {
                Map<?, ?> criteriaMap = (Map<?, ?>) criteria;

                for (Map.Entry<?, ?> entry : criteriaMap.entrySet()) {
                    String key = entry.getKey().toString();
                    Object value = entry.getValue();

                    if (value != null && !value.toString().trim().isEmpty()) {
                        // Direct XML elements without wrapper, matching SOAP UI format
                        xmlBuilder.append("<").append(key).append(">")
                                 .append(escapeXml(value.toString()))
                                 .append("</").append(key).append(">");
                    }
                }
            } else if (criteria instanceof String) {
                // If it's a String, wrap in <idSaveEfact>
                xmlBuilder.append("<idSaveEfact>").append(escapeXml(criteria.toString())).append("</idSaveEfact>");
            } else {
                // If it's not a Map or String, try to convert it to a simple element
                logger.warn("Criteria is not a Map, attempting to serialize: {}", criteria.getClass().getSimpleName());
                xmlBuilder.append("<data>").append(escapeXml(criteria.toString())).append("</data>");
            }
        } catch (Exception e) {
            logger.error("Error serializing criteria to XML: {}", e.getMessage(), e);
            xmlBuilder.append("<error>Failed to serialize criteria</error>");
        }

        return xmlBuilder.toString();
    }

    /**
     * Parse the SOAP response from consultEfact operation
     */
    private TtnConsultEfactResponse parseConsultEfactResponse(String soapResponse) {
        TtnConsultEfactResponse response = new TtnConsultEfactResponse();

        try {
            // Check if it's a SOAP fault
            if (soapResponse.contains("<S:Fault") || soapResponse.contains("<soap:Fault")) {
                response.setSuccess(false);

                // Extract fault message
                Pattern faultPattern = Pattern.compile("<faultMessage>([^<]+)</faultMessage>");
                Matcher faultMatcher = faultPattern.matcher(soapResponse);

                if (faultMatcher.find()) {
                    response.setError(faultMatcher.group(1));
                } else {
                    response.setError("SOAP Fault occurred");
                }

                response.setCount(0);
                response.setInvoices(new ArrayList<>());
                return response;
            }

            // Parse successful response
            if (soapResponse.contains("<ns2:consultEfactResponse") || soapResponse.contains("consultEfactResponse")) {
                response.setSuccess(true);

                // Count the number of <return> elements (each represents an invoice)
                Pattern returnPattern = Pattern.compile("<return>.*?</return>", Pattern.DOTALL);
                Matcher returnMatcher = returnPattern.matcher(soapResponse);

                List<Object> invoices = new ArrayList<>();
                int count = 0;

                while (returnMatcher.find()) {
                    count++;
                    String returnXml = returnMatcher.group();

                    // Parse individual invoice data
                    Map<String, Object> invoice = parseInvoiceFromXml(returnXml);
                    invoices.add(invoice);
                }

                response.setCount(count);
                response.setInvoices(invoices);

                logger.info("Successfully parsed {} invoices from consultEfact response", count);
            } else {
                response.setSuccess(false);
                response.setError("Unexpected response format");
                response.setCount(0);
                response.setInvoices(new ArrayList<>());
            }

        } catch (Exception e) {
            logger.error("Error parsing consultEfact response: {}", e.getMessage(), e);
            response.setSuccess(false);
            response.setError("Failed to parse response: " + e.getMessage());
            response.setCount(0);
            response.setInvoices(new ArrayList<>());
        }

        return response;
    }

    /**
     * Parse individual invoice data from XML return element
     */
    private Map<String, Object> parseInvoiceFromXml(String returnXml) {
        Map<String, Object> invoice = new HashMap<>();

        try {
            // Extract common fields
            invoice.put("amount", extractXmlValue(returnXml, "amount"));
            invoice.put("amountTax", extractXmlValue(returnXml, "amountTax"));
            invoice.put("dateDocument", extractXmlValue(returnXml, "dateDocument"));
            invoice.put("dateProcess", extractXmlValue(returnXml, "dateProcess"));
            invoice.put("documentNumber", extractXmlValue(returnXml, "documentNumber"));
            invoice.put("documentType", extractXmlValue(returnXml, "documentType"));

            // Extract acknowledgments and errors if present
            if (returnXml.contains("<listAcknowlegments>")) {
                Map<String, Object> acknowledgments = new HashMap<>();
                acknowledgments.put("dateAck", extractXmlValue(returnXml, "dateAck"));

                // Extract errors
                if (returnXml.contains("<errors>")) {
                    Map<String, Object> errors = new HashMap<>();
                    errors.put("errorDescription", extractXmlValue(returnXml, "errorDescription"));
                    errors.put("errorId", extractXmlValue(returnXml, "errorId"));
                    acknowledgments.put("errors", errors);
                }

                invoice.put("acknowledgments", acknowledgments);
            }

            // Set ID and reference for compatibility
            invoice.put("id", extractXmlValue(returnXml, "documentNumber"));
            invoice.put("reference", extractXmlValue(returnXml, "documentNumber"));
            invoice.put("status", extractXmlValue(returnXml, "documentType"));
            invoice.put("date", extractXmlValue(returnXml, "dateDocument"));

        } catch (Exception e) {
            logger.error("Error parsing invoice XML: {}", e.getMessage(), e);
            invoice.put("error", "Failed to parse invoice data");
        }

        return invoice;
    }

    /**
     * Extract value from XML element
     */
    private String extractXmlValue(String xml, String elementName) {
        Pattern pattern = Pattern.compile("<" + elementName + ">([^<]*)</" + elementName + ">");
        Matcher matcher = pattern.matcher(xml);
        return matcher.find() ? matcher.group(1) : null;
    }

    /**
     * Escape XML special characters
     */
    private String escapeXml(String input) {
        if (input == null) return "";
        return input.replace("&", "&amp;")
                   .replace("<", "&lt;")
                   .replace(">", "&gt;")
                   .replace("\"", "&quot;")
                   .replace("'", "&apos;");
    }

    private String sendSoapRequest(String soapRequest) {
        HttpHeaders headers = new HttpHeaders();
        headers.setContentType(MediaType.TEXT_XML);
        headers.set("SOAPAction", "");

        HttpEntity<String> entity = new HttpEntity<>(soapRequest, headers);

        ResponseEntity<String> response = restTemplate.postForEntity(soapUrl, entity, String.class);

        if (response.getStatusCode().is2xxSuccessful()) {
            return response.getBody();
        } else {
            throw new RuntimeException("SOAP request failed with status: " + response.getStatusCode());
        }
    }

    private String extractReferenceFromResponse(String soapResponse) {
        // Extract reference from SOAP response
        // Pattern: "Facture enregistree avec ID 1805137 est en cours de validation"
        Pattern pattern = Pattern.compile("<return>(.*?)</return>");
        Matcher matcher = pattern.matcher(soapResponse);

        if (matcher.find()) {
            return matcher.group(1);
        }

        return soapResponse; // Return full response if pattern not found
    }
}

package com.example.unifiedapi.service;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.*;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;

import java.util.Base64;
import java.util.HashMap;
import java.util.Map;

@Service
public class TtnTransformationService {

    private static final Logger logger = LoggerFactory.getLogger(TtnTransformationService.class);

    private static final String TRANSFORMATION_URL = "https://test.elfatoora.tn/ElfatouraServicesRest/rest/api/transform";

    private final RestTemplate restTemplate;
    private final OperationLogService operationLogService;
    private final UserCredentialsService userCredentialsService;

    @Autowired
    public TtnTransformationService(RestTemplate restTemplate, OperationLogService operationLogService, UserCredentialsService userCredentialsService) {
        this.restTemplate = restTemplate;
        this.operationLogService = operationLogService;
        this.userCredentialsService = userCredentialsService;
    }

    /**
     * Transform XML invoice to HTML using TTN transformation service
     * 
     * @param xmlContent The signed XML content
     * @param username TTN username
     * @param password TTN password
     * @param matriculeFiscal TTN matricule fiscal
     * @param filename Original filename for logging
     * @return HTML content as string
     * @throws Exception if transformation fails
     */
    public String transformXmlToHtml(String base64XmlContent, String username, String password, 
                                   String matriculeFiscal, String filename) throws Exception {
        logger.info("Starting XML to HTML transformation for file: {}", filename);
        try {
            // Use credentials from UserCredentialsService if not provided
            String user = (username == null || username.isEmpty()) ? userCredentialsService.getTtnUsername() : username;
            String pass = (password == null || password.isEmpty()) ? userCredentialsService.getTtnPassword() : password;
            String matricule = (matriculeFiscal == null || matriculeFiscal.isEmpty()) ? userCredentialsService.getTtnMatriculeFiscal() : matriculeFiscal;

            // Use the base64-encoded xmlContent directly (from TTN consult)
            Map<String, String> requestPayload = new HashMap<>();
            requestPayload.put("login", user);
            requestPayload.put("password", pass);
            requestPayload.put("matricule", matricule);
            requestPayload.put("documentEfact", base64XmlContent);

            // Set headers
            HttpHeaders headers = new HttpHeaders();
            headers.setContentType(MediaType.APPLICATION_JSON);
            headers.set("Accept", MediaType.TEXT_HTML_VALUE);

            HttpEntity<Map<String, String>> request = new HttpEntity<>(requestPayload, headers);

            logger.debug("Sending transformation request to: {}", TRANSFORMATION_URL);

            // Make the REST call
            ResponseEntity<String> response = restTemplate.postForEntity(TRANSFORMATION_URL, request, String.class);

            if (response.getStatusCode().is2xxSuccessful()) {
                String htmlContent = response.getBody();

                if (htmlContent != null && !htmlContent.trim().isEmpty()) {
                    logger.info("XML to HTML transformation completed successfully for file: {}", filename);

                    // Log successful operation
                    operationLogService.logOperationWithDetails(
                        "TTN_TRANSFORM", "SUCCESS", (long) base64XmlContent.length(), filename,
                        "XML to HTML transformation completed", user, matricule, null
                    );

                    return htmlContent;
                } else {
                    throw new RuntimeException("Empty HTML response received from transformation service");
                }
            } else {
                throw new RuntimeException("Transformation service returned error: " + response.getStatusCode());
            }

        } catch (Exception e) {
            logger.error("XML to HTML transformation failed for file {}: {}", filename, e.getMessage(), e);

            // Log failed operation
            operationLogService.logOperationWithDetails(
                "TTN_TRANSFORM", "FAILURE", (long) base64XmlContent.length(), filename,
                "XML to HTML transformation failed", username, matriculeFiscal, e.getMessage()
            );

            throw new RuntimeException("Failed to transform XML to HTML: " + e.getMessage(), e);
        }
    }

    /**
     * Transform multiple XML files to HTML
     * 
     * @param xmlContents Map of filename to XML content
     * @param username TTN username
     * @param password TTN password
     * @param matriculeFiscal TTN matricule fiscal
     * @return Map of filename to HTML content
     */
    public Map<String, String> transformMultipleXmlToHtml(Map<String, String> xmlContents, 
                                                         String username, String password, 
                                                         String matriculeFiscal) {

        Map<String, String> htmlResults = new HashMap<>();

        for (Map.Entry<String, String> entry : xmlContents.entrySet()) {
            String filename = entry.getKey();
            String xmlContent = entry.getValue();

            try {
                String htmlContent = transformXmlToHtml(xmlContent, username, password, matriculeFiscal, filename);
                htmlResults.put(filename, htmlContent);
            } catch (Exception e) {
                logger.error("Failed to transform file {}: {}", filename, e.getMessage());
                // Continue with other files, don't fail the entire batch
                htmlResults.put(filename, createErrorHtml(filename, e.getMessage()));
            }
        }

        return htmlResults;
    }

    /**
     * Create error HTML content when transformation fails
     */
    private String createErrorHtml(String filename, String errorMessage) {
        return String.format(
            "<!DOCTYPE html>\n" +
            "<html>\n" +
            "<head>\n" +
            "    <title>Transformation Error - %s</title>\n" +
            "    <style>\n" +
            "        body { font-family: Arial, sans-serif; margin: 40px; }\n" +
            "        .error { color: #d32f2f; background: #ffebee; padding: 20px; border-radius: 4px; }\n" +
            "    </style>\n" +
            "</head>\n" +
            "<body>\n" +
            "    <h1>Transformation Error</h1>\n" +
            "    <div class=\"error\">\n" +
            "        <h3>File: %s</h3>\n" +
            "        <p><strong>Error:</strong> %s</p>\n" +
            "        <p>The XML to HTML transformation failed for this file. Please check the XML format and try again.</p>\n" +
            "    </div>\n" +
            "</body>\n" +
            "</html>",
            filename, filename, errorMessage
        );
    }

    /**
     * Validate if the transformation service is available
     */
    public boolean isTransformationServiceAvailable() {
        try {
            // Simple health check - you might want to implement a proper health endpoint
            HttpHeaders headers = new HttpHeaders();
            headers.set("Accept", MediaType.APPLICATION_JSON_VALUE);

            HttpEntity<String> request = new HttpEntity<>(headers);
            ResponseEntity<String> response = restTemplate.exchange(
                TRANSFORMATION_URL, HttpMethod.OPTIONS, request, String.class
            );

            return response.getStatusCode().is2xxSuccessful();
        } catch (Exception e) {
            logger.warn("Transformation service health check failed: {}", e.getMessage());
            return false;
        }
    }
}

