    init() {
        this.setupEventListeners();
        this.checkAuthentication();
    }

    setupEventListeners() {
        const loginForm = document.getElementById('loginForm');
        if (loginForm) {
            loginForm.addEventListener('submit', (e) => {
                e.preventDefault();
                this.login();
            });
        }
    }

    checkAuthentication() {
        if (this.auth.isAuthenticated()) {
            this.showDashboard();
            this.overview.loadOverview();
        } else {
            this.showLogin();
        }
    }

    showLogin() {
        const login = document.getElementById('loginSection');
        const dash = document.getElementById('dashboardSection');
        if (login) login.style.display = 'block';
        if (dash) dash.classList.remove('active');
    }

    showDashboard() {
        const login = document.getElementById('loginSection');
        const dash = document.getElementById('dashboardSection');
        if (login) login.style.display = 'none';
        if (dash) dash.classList.add('active');
    }

    async login() {
        const email = document.getElementById('email').value;
        const password = document.getElementById('password').value;
        const alertDiv = document.getElementById('loginAlert');

        try {
            const data = await this.auth.login(email, password);
            this.currentUser = data.user || null;
            const currentUserEl = document.getElementById('currentUser');
            if (currentUserEl && this.currentUser) {
                currentUserEl.textContent = this.currentUser.username || this.currentUser.email || '';
            }
            this.showDashboard();
            this.overview.loadOverview();
            if (alertDiv) alertDiv.style.display = 'none';
        } catch (error) {
            if (alertDiv) {
                alertDiv.textContent = error.message;
                alertDiv.style.display = 'block';
            }
        }
    }

    logout() {
        this.auth.logout();
        this.currentUser = null;
        this.showLogin();
    }

